    # first range of each chromosome is pathogenic, second range is not
    pathogenic_or_not_pathogenic = {
        'chr11': [[108093559, 108236203], [108236204, 108239826], 'ATM'],
        'chr13': [[32889611, 32972575], [32972576, 32973347], 'BRCA2'],
        'chr17': [[41197717, 41277387], [41196312, 41197716], 'BRCA1'],
        'chr22': [[29083970, 29137826], [29083751, 29083969], 'CHEK2']
    }

    stop_codons_MT = ['AGA', 'AGG']
    stop_codons = ['TAA', 'TAG', 'TGA']

    variant = input_dialog()
    # variant = 'chr11:108153470A>T'
    # variant = 'chr11:108153470A>GATTACAT'
    # variant = 'chr11:108153470000A>GATTACA'
    # variant = 'chr11:108153470A>GATTAGA'
    # variant = 'chr11:108153470AGA>A'

    variant_bases = ''.join(char for char in variant.split(':')[-1] if not char.isnumeric()).split('>')
    variant_ref = variant_bases[0]
    variant_alt = variant_bases[1]
    variant_chr = variant.split(':')[0]
    variant_pos = int(''.join(char for char in variant.split(':')[-1] if char.isnumeric()))
    variant_pos_end = variant_pos + max(len(variant_ref), len(variant_alt)) - 1
    # print('The variant', variant, 'spans on', [variant_pos, variant_pos_end], 'positions of', variant_chr, '...')

    seq_chr11 = ''.join([x[2] for x in ref_dict['chr11']])
    seq_chr13 = ''.join([x[2] for x in ref_dict['chr13']])
    seq_chr17 = ''.join([reverse_complement(x[2]) for x in ref_dict['chr17']])
    seq_chr22 = ''.join([reverse_complement(x[2]) for x in ref_dict['chr22']])

    # seq_chr11_split = [seq_chr11[_:_ + 3] for _ in range(0, len(seq_chr11), 3)]
    # seq_chr11_stop = any([x for x in stop_codons if x in seq_chr11_split[-5:]])
    #
    # seq_chr13_split = [seq_chr13[_:_ + 3] for _ in range(0, len(seq_chr13), 3)]
    # seq_chr13_stop = any([x for x in stop_codons if x in seq_chr13_split[-5:]])
    #
    # seq_chr17_split = [seq_chr17[_:_ + 3] for _ in range(0, len(seq_chr17), 3)]
    # seq_chr17_stop = any([x for x in stop_codons if x in seq_chr17_split[-5:]])
    #
    # seq_chr22_split = [seq_chr22[_:_ + 3] for _ in range(0, len(seq_chr22), 3)]
    # seq_chr22_stop = any([x for x in stop_codons if x in seq_chr22_split[-5:]])
    #
    # print('seq_chr11_stop', seq_chr11_stop, 'seq_chr13_stop', seq_chr13_stop,
    #       'seq_chr17_stop', seq_chr17_stop, 'seq_chr22_stop', seq_chr22_stop)
    # print('seq_chr11_split[-5:]', seq_chr11_split[-5:])
    # print('seq_chr13_split[-5:]', seq_chr13_split[-5:])
    # print('seq_chr17_split[-5:]', seq_chr17_split[-5:])
    # print('seq_chr22_split[-5:]', seq_chr22_split[-5:])

    # considering start of the variant normal (not previously shifted)
    try:
        chrom_range = ref_dict[variant_chr]
        # print('chrom_range', chrom_range)
        chrom_seq = ''.join([x[2] for x in ref_dict[variant_chr]]) \
            if variant_chr == 'chr11' or variant_chr == 'chr13' \
            else ''.join([reverse_complement(x[2]) for x in ref_dict[variant_chr]])

        yay = 0
        for v in chrom_range:
            if v[0] <= variant_pos and variant_pos_end <= v[1]:
                yay += 1
                # print('Yay - variant belongs to the ABC region, and we can cut out its own little region')
                seq_ref = v[2]
                dist_to_start = int(variant_pos) - int(v[0])
                dist_to_start_full = int(variant_pos) - int(v[0])
                # print('dist_to_start', dist_to_start)
                # dist_to_start = dist_to_start - 2 if dist_to_start >= 2 else dist_to_start
                # print('dist_to_start', dist_to_start)
                seq_ref_mini = seq_ref[dist_to_start:dist_to_start + len(variant_ref) + 2]
                seq_alt_mini = variant_alt + seq_ref[
                                             dist_to_start + len(variant_ref):dist_to_start + len(variant_ref) + 2]

                seq_ref_mini_c = reverse_complement(seq_ref[dist_to_start - 2:dist_to_start + len(variant_ref)])
                seq_alt_mini_c = reverse_complement(seq_ref[dist_to_start - 2:dist_to_start] + variant_alt)

                seq_ref_bothends = seq_ref[dist_to_start - 2:dist_to_start + len(variant_ref) + 2]
                seq_ref_bothends_c = reverse_complement(seq_ref[dist_to_start - 2:dist_to_start + len(variant_ref) + 2])
                seq_alt_bothends = seq_ref[dist_to_start - 2:dist_to_start] + variant_alt + \
                                   seq_ref[dist_to_start + len(variant_ref):dist_to_start + len(variant_ref) + 2]
                seq_alt_bothends_c = reverse_complement(seq_ref[dist_to_start - 2:dist_to_start] + variant_alt +
                                                        seq_ref[dist_to_start + len(variant_ref):dist_to_start + len(
                                                            variant_ref) + 2])

                # print('Variant:', variant_ref, '>>>', variant_alt)
                # print('Variant + next 2 bases:', seq_ref_mini, '(' + seq_ref_mini_c + ')',
                #       '>>>', seq_alt_mini, '(' + seq_alt_mini_c + ')')
                # print('Prev 2 bases + variant + next 2 bases: ' +
                #       seq_ref_bothends, '(' + seq_ref_bothends_c + ')' + ' >>> ' +
                #       seq_alt_bothends, '(' + seq_alt_bothends_c + ')\n')

                seq_alt_split = [seq_alt_mini[_:_ + 3] for _ in range(0, len(seq_alt_mini), 3)]
                seq_alt_c_split = [seq_alt_mini_c[_:_ + 3] for _ in range(0, len(seq_alt_mini_c), 3)]
                # print('seq_alt_split', seq_alt_split)
                # print('seq_alt_c_split', seq_alt_c_split)

                # detecting stop codon: alt sequence + next 2 bases includes stop codon
                if 'MT' in variant_chr:
                    is_stop_codon = any([x for x in stop_codons_MT if x in seq_alt_split or x in seq_alt_c_split])
                    is_stop_codon_c = any([x for x in stop_codons_MT if x in seq_alt_c_split])
                else:
                    is_stop_codon = any([x for x in stop_codons if x in seq_alt_split or x in seq_alt_c_split])
                    is_stop_codon_c = any([x for x in stop_codons if x in seq_alt_c_split])
                # print('is_stop_codon', is_stop_codon)

                # detecting frameshift: length of the change from the insertion or deletion is not divisible by 3
                is_frameshift = True if len(variant_alt) != len(variant_ref) and \
                                        abs(len(variant_alt) - len(variant_ref)) % 3 != 0 else False
                # print('is_frameshift', is_frameshift)

                # the variant is considered pathogenic if is within designated positions
                curr_ponp = pathogenic_or_not_pathogenic[variant_chr]
                is_in_pathogenic_range = curr_ponp[0][0] <= variant_pos <= curr_ponp[0][1]
                is_in_non_pathogenic_range = curr_ponp[1][0] <= variant_pos <= curr_ponp[1][1]
                if is_in_pathogenic_range and (is_stop_codon or is_frameshift):
                    is_pathogenic = True
                elif is_in_non_pathogenic_range:
                    is_pathogenic = False
                else:
                    is_pathogenic = False
                # print('is_pathogenic', is_pathogenic)

                # print('Verdict: variant', 'is' if is_pathogenic else 'is NOT', 'pathogenic',
                #       'because it belongs to the',
                #       'pathogenic' if is_in_pathogenic_range else 'non-pathogenic',
                #       'range', curr_ponp[0], 'of', curr_ponp[2], 'gene',
                #       'while', 'being' if is_stop_codon else 'NOT being', 'a stop codon',
                #       'and', 'being' if is_frameshift else 'NOT being', 'a frameshift')
                print_is_pathogenic = str(1) if is_pathogenic else str(0)
                print_is_frameshift = str(1) if is_frameshift else str(0)
                print_is_stop_codon = str(1) if is_stop_codon else str(0)
                # print_seq = seq_ref_bothends + '>' + seq_alt_bothends + '_' + \
                #             seq_ref_bothends_c + '>' + seq_alt_bothends_c if is_stop_codon or is_frameshift else ''
                print_seq = 'f_' + seq_ref_mini + '>' + seq_alt_mini if is_frameshift else ''
                print_seq = 'f_' + seq_ref_bothends + '>' + seq_alt_bothends if is_stop_codon else print_seq
                print_seq = 'r_' + seq_ref_bothends_c + '>' + seq_alt_bothends_c if is_stop_codon_c else print_seq
                print(variant + ',' + print_is_pathogenic + ',' + print_is_frameshift + ',' + print_is_stop_codon +
                      ',' + print_seq)

                break
        if yay == 0:
            print('Oh no! Variant\'s position', str(variant_chr) + ':' + str(variant_pos),
                  'is not in the designated chromosome range! Choose wisely ...')

    except KeyError as e:
        print('Oh no! Variant\'s chromosome', e,
              'is not in the ABC region! Choose wisely ...')
